import { useState, useEffect } from "react";
import "./Header.css";

function Header() {
  const [zoomLevel, setZoomLevel] = useState(100);

  useEffect(() => {
    const calculateZoomLevel = () => {
      setZoomLevel(Math.round(window.devicePixelRatio * 100));
    };

    calculateZoomLevel();
    window.addEventListener("resize", calculateZoomLevel);
    return () => window.removeEventListener("resize", calculateZoomLevel);
  }, []);

  // Dynamic styles that *must* remain in JS
  const logoStyle = {
    height: "75px",
    position: "absolute",
    top: "23px",
    left:
      zoomLevel < 175
        ? "330px"
        : zoomLevel <= 250
        ? "120px"
        : "-5px",
  };
  const useIsFullscreen = () => {
  const [isFullscreen, setIsFullscreen] = useState(false);

  useEffect(() => {
    const handleFullscreenChange = () => {
      // document.fullscreenElement will be non-null if in fullscreen mode
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    document.addEventListener('mozfullscreenchange', handleFullscreenChange); // For Firefox compatibility
    document.addEventListener('webkitfullscreenchange', handleFullscreenChange); // For Chrome, Safari, Opera compatibility

    // Initial check on mount
    handleFullscreenChange();

    return () => {
      document.removeEventListener('fullscreenchange', handleFullscreenChange);
      document.removeEventListener('mozfullscreenchange', handleFullscreenChange);
      document.removeEventListener('webkitfullscreenchange', handleFullscreenChange);
    };
  }, []);

  return isFullscreen;
}

const isFullscreen = useIsFullscreen()
  const titleStyle = {
    fontSize: "2.8rem",
    fontWeight: "200",
    transform: "skew(-6deg)",
    position: "absolute",
    top: "35px",
    left:
      zoomLevel < 175
        ? "388px"
        : zoomLevel <= 250
        ? "178px"
        : "53px",
  };

  const btnCommon = {
    backgroundColor: "#333",
    color: "#fff",
    border: "0",
    cursor: "pointer",
    position: "absolute",
    height: zoomLevel <= 250 ? "65px" : "45px",
    width: zoomLevel <= 250 ? "150px" : "105px",
    fontSize: zoomLevel <= 250 ? "18px" : "13px",
    top: zoomLevel <= 250 ? "35px" : "45px",
  };

  return (
    <header>
      <h1 className="header-top">
        <img
          src={process.env.PUBLIC_URL + "/lock.png"}
          alt="logo"
          style={logoStyle}
        />

        <i style={titleStyle}>
          sudo<b>Securee {isFullscreen ? 'true': 'faflse'}zz</b>
        </i>


        <button
          onClick={() => {
            window.location.replace("#GoodP");
          }}
          style={{
            ...btnCommon,
            right:
              zoomLevel < 175
                ? "515px"
                : zoomLevel <= 250
                ? "305px"
                : "105px",
            borderTopLeftRadius: "9px",
            borderBottomLeftRadius: "9px",
          }}
        >
          Good
          <br />
          Practices
        </button>

        <button
          onClick={() => {
            window.location.replace("#FAQ");
          }}
          style={{
            ...btnCommon,
            right:
              zoomLevel < 175
                ? "365px"
                : zoomLevel <= 250
                ? "155px"
                : "0px",
            borderTopRightRadius: "9px",
            borderBottomRightRadius: "9px",
          }}
        >
          FAQ
        </button>
      </h1>

      <h1 className="header-bottom">
        <b
          className="password-title"
          style={{
            height: zoomLevel < 400 ? zoomLevel < 250? "60px": '80px' : "110px",
            top: "0px",
          }}
        >
          <b>HOW SECURE IS YOUR PASSWORD?</b>
        </b>
      </h1>
    </header>
  );
}

export default Header;
